package core

import (
	clock "bash-payments-payserv/pkg/clock"
	sentrylogger "bash-payments-payserv/pkg/sentry-logger"
	"errors"
	"time"

	"github.com/gofrs/uuid"
)

// -------------------------
// Create
// -------------------------

func CreatePaymentInstruction(
	logger sentrylogger.Logger,
	paymentInstructionRepository PaymentInstructionRepository,
	sourceRepository SourceRepository,
	sourceConfigRepository SourceConfigRepository,
	eventService PaymentInstructionEventService,
	data CreatePaymentInstructionInput,
	timeToExpireSeconds int,
) (PaymentInstruction, error) {
	source, err := sourceRepository.GetByKey(data.SourceKey)
	if err != nil {
		return PaymentInstruction{}, err
	}

	// if payment instruction already exists, we return the existing payment instruction
	var idempotencyKey *string
	if data.IdempotencyKey != "" {
		idempotencyKey = &data.IdempotencyKey
		paymentInstruction, err := paymentInstructionRepository.GetBySourceIdAndIdempotencyKey(source.Id, data.IdempotencyKey)
		if err != nil {
			return PaymentInstruction{}, err
		}
		if paymentInstruction != nil {
			return *paymentInstruction, nil
		}
	}

	// otherwise we create and return a new payment instruction
	id, err := uuid.NewV7()
	if err != nil {
		logger.Error(err)
		return PaymentInstruction{}, err
	}
	var sourceCallbackUrl *string = nil

	if data.SourceCallbackUrl != "" {
		sourceCallbackUrl = &data.SourceCallbackUrl
	}
	internalWillExpireAt := time.Now().UTC().Add(time.Duration(timeToExpireSeconds) * time.Second)

	sourceConfig, err := sourceConfigRepository.GetBySourceKey(data.SourceKey)
	if err != nil {
		return PaymentInstruction{}, err
	}

	if sourceConfig == nil {
		return PaymentInstruction{}, SourceConfigDoesNotExistError{}
	}
	paymentInstructionInput := PaymentInstruction{
		Id:             id,
		AmountCents:    data.AmountCents,
		SourceId:       source.Id,
		ClientKey:      &data.ClientKey,
		IdempotencyKey: idempotencyKey,
		// TODO: add clientId
		SourceCallbackUrl:        sourceCallbackUrl,
		SourceReference:          data.SourceReference,
		SourceTransactionId:      &data.SourceTransactionId,
		SourcePaymentId:          &data.SourcePaymentId,
		CustomerId:               &data.CustomerId,
		SourceSuccessRedirectUrl: data.SourceSuccessRedirectUrl,
		SourceFailureRedirectUrl: data.SourceFailureRedirectUrl,
		Type:                     data.Type,
		InternalWillExpireAt:     &internalWillExpireAt,
		IsCancellable:            sourceConfig.AllowPaymentInstructionCancellation,
	}
	paymentInstruction, err := paymentInstructionRepository.Create(paymentInstructionInput)
	if err != nil {
		//metrics.PaymentInstructionErrorCounter.WithLabelValues("failed.to.persist.to.db").Inc()
		return PaymentInstruction{}, err
	}
	err = eventService.PublishPaymentInstructionEvent(PAYMENT_INSTRUCTION_CREATED_EVENT, *paymentInstruction)
	if err != nil {
		//metrics.PaymentInstructionErrorCounter.WithLabelValues("failed.to.publish.event").Inc()
		return PaymentInstruction{}, err
	}

	return *paymentInstruction, nil
}

func CompletePaymentInstruction(
	logger sentrylogger.Logger,
	providerClients map[ProviderKey]ProviderClient,
	paymentRepository PaymentRepository,
	paymentInstructionRepository PaymentInstructionRepository,
	paymentInstructionEventService PaymentInstructionEventService,
	sourcePaymentMethodConfigRepository SourcePaymentMethodConfigRepository,
	id uuid.UUID) error {
	// get payment instruction
	paymentInstruction, err := paymentInstructionRepository.GetById(id)
	if err != nil {
		dbErr := CompletePaymentError{Message: "failed to get payment instruction from the db"}
		logger.Error(dbErr, sentrylogger.KV("error", err), sentrylogger.KV("payment_instruction_id", id))
		return err
	}
	if paymentInstruction == nil {
		dbErr := CompletePaymentError{Message: "failed to get payment instruction from the db"}
		logger.Error(dbErr, sentrylogger.KV("error", err), sentrylogger.KV("payment_instruction_id", id))
		return err
	}
	if paymentInstruction.InternalCompletedAt != nil {
		return nil
	}

	for _, payment := range paymentInstruction.Payments {

		if payment.Status != PAYMENT_SUCCEEDED {
			continue
		}
		// get provider payment method config
		sourcePaymentMethodConfig, err := sourcePaymentMethodConfigRepository.GetBySourceIdAndPaymentMethodKey(paymentInstruction.SourceId, payment.PaymentMethod.Key)
		if err != nil {
			_err := GetPaymentInstrumentError{Message: "failed to get source payment method config"}
			logger.Error(_err, sentrylogger.KV("payment", payment))
			return _err
		}
		if sourcePaymentMethodConfig == nil {
			err := CompletePaymentInstructionError{Message: "payment method not supported by source"}
			logger.Error(err, sentrylogger.KV("error", err), sentrylogger.KV("payment_id", payment.Id))
			return err
		}

		// get provider client
		providerClient, ok := providerClients[sourcePaymentMethodConfig.ProviderKey]
		if !ok {
			err := CompletePaymentInstructionError{Message: "payment provider has no corresponding provider client"}
			logger.Error(err, sentrylogger.KV("error", err), sentrylogger.KV("payment_id", payment.Id))
			return err
		}

		// complete payment via provider client
		completedPayment, err := providerClient.CompletePayment(paymentInstruction, payment)
		if err != nil {
			_err := CompletePaymentInstructionError{Message: "error completing payment"}
			logger.Error(_err, sentrylogger.KV("error", err), sentrylogger.KV("payment_id", payment.Id))
			return _err
		}
		// update payment provider details
		_, err = paymentRepository.UpdateProviderDetails(completedPayment.Id, completedPayment.ProviderPayment.Details)
		if err != nil {
			dbErr := CompletePaymentError{Message: "failed to update payment provider details"}
			logger.Error(dbErr, sentrylogger.KV("error", err), sentrylogger.KV("payment_id", completedPayment.Id))
			return err
		}
	}

	// update payment instruction to completed
	completedAt := time.Now()
	_, err = paymentInstructionRepository.UpdateToCompleted(paymentInstruction.Id, completedAt)
	if err != nil {
		dbErr := CompletePaymentError{Message: "failed to update payment instruction"}
		logger.Error(dbErr, sentrylogger.KV("error", err), sentrylogger.KV("payment_instruction_id", id))
		return err
	}

	// re-fetch payment instruction
	paymentInstruction, err = paymentInstructionRepository.GetById(id)

	// publish payment instruction completed event
	if err := paymentInstructionEventService.PublishPaymentInstructionEvent(PAYMENT_INSTRUCTION_COMPLETED_EVENT, *paymentInstruction); err != nil {
		kafkaErr := CompletePaymentError{Message: "failed to publish payment instruction completed event"}
		logger.Error(kafkaErr, sentrylogger.KV("error", err), sentrylogger.KV("payment_instruction_id", id))
		return err
	}

	return nil
}

func UpdatePaymentInstructionToFailed(
	logger sentrylogger.Logger,
	paymentInstructionRepository PaymentInstructionRepository,
	paymentInstructionId uuid.UUID,
) error {
	paymentInstruction, err := paymentInstructionRepository.GetById(paymentInstructionId)
	id := paymentInstructionId
	if err != nil {
		dbErr := CompletePaymentError{Message: "failed to get payment instruction from the db"}
		logger.Error(dbErr, sentrylogger.KV("error", err), sentrylogger.KV("payment_instruction_id", id))
		return err
	}
	if paymentInstruction == nil {
		dbErr := CompletePaymentError{Message: "failed to get payment instruction from the db"}
		logger.Error(dbErr, sentrylogger.KV("error", err), sentrylogger.KV("payment_instruction_id", id))
		return err
	}

package dbmigrateconfig

import (
	"bash-payments-payserv/internal/config"

	"github.com/spf13/viper"
)

// LoadConfigFromEnvs
// - will populate Config struct with values from environment variables and .env file
// - will not error if no .env file exists, only if it exists and is invalid
// - will not validate config values, so functions/methods using these configs need to do that
// - all config values are strings, so functions/methods using these configs need to convert them to appropriate types
// - environment variables will take precedence over values in the .env file
func LoadConfigFromEnvs(configName string, configType string, configPath string, _config *Config) error {
	// set defaults
	// ... logger
	viper.SetDefault("LOGGER_ENCODING", "console")

	// bind envs (required to load config from environment variables)
	viper.BindEnv("ENV")
	// ... logger
	viper.BindEnv("LOGGER_ENCODING")
	// ... secrets
	viper.BindEnv("API_SECRET")
	viper.BindEnv("CLIENT_API_KEYS_SECRET")
	// ... aws
	viper.BindEnv("AWS_REGION")
	// ... db
	viper.BindEnv("DB_DSN")
	viper.BindEnv("DB_HOST")
	viper.BindEnv("DB_NAME")
	viper.BindEnv("DB_PORT")
	viper.BindEnv("DB_USER")
	viper.BindEnv("DB_PASSWORD")
	viper.BindEnv("DB_SSLMODE")
	viper.BindEnv("DB_MIGRATIONS_PATH")

	// Load the configuration from environment variables
	viper.AutomaticEnv()

	// Load the configuration from file
	viper.SetConfigName(configName)
	viper.SetConfigType(configType)
	viper.AddConfigPath(configPath)

	err := viper.ReadInConfig()
	if err != nil {
		switch err.(type) {
		// ... ignore if config file not found
		case viper.ConfigFileNotFoundError:
			break
		// ... otherwise return error
		default:
			return err
		}
	}

	// Deserialize the configuration into the config struct
	err = viper.Unmarshal(&_config)
	err = viper.Unmarshal(&_config.Aws)
	err = viper.Unmarshal(&_config.Db)
	if err != nil {
		return err
	}
	return err
}

func LoadConfigFromAwsSecrets(awsService AWSService, _config *Config) error {
	// AWS
	if _config.Aws.Region == "" {
		return nil
	}
	if err := awsService.SetRegion(_config.Aws.Region); err != nil {
		return err
	}

	// secret names
	apiSecretName := _config.ApiSecret

	// API secret
	if apiSecretName != "" {
		decodedSecret := config.ApiSecret{}
		err := awsService.DecodeSecret(apiSecretName, &decodedSecret)
		if err != nil {
			return &LoadConfigError{Message: "[Config] error loading secret: " + apiSecretName, OriginalError: err}
		}
		// database
		if _config.Db.User == "" {
			_config.Db.User = decodedSecret.User
		}
		if _config.Db.Password == "" {
			_config.Db.Password = decodedSecret.Password
		}
	}

	return nil
}

func LoadConfig(awsService AWSService, configName string, configType string, configPath string) (config Config, err error) {
	_config := Config{}

	// envs
	err = LoadConfigFromEnvs(configName, configType, configPath, &_config)
	if err != nil {
		return _config, err
	}

	// AWS secrets
	err = LoadConfigFromAwsSecrets(awsService, &_config)
	if err != nil {
		return _config, err
	}

	return _config, nil
}

