-- We are making the source_id nullable because existing records do not have a source_id.
-- After this migration is run, we need to populate the source_id for existing records 
-- and run another migration to make this column non-nullable.
ADD COLUMN IF NOT EXISTS source_id UUID REFERENCES source (id) DEFAULT NULL,
ADD COLUMN IF NOT EXISTS idempotency_key VARCHAR(255) DEFAULT NULL;

BEGIN;
ALTER TABLE payment_instruction
DROP CONSTRAINT IF EXISTS fk_source_id;
ALTER TABLE payment_instruction
ADD CONSTRAINT fk_source_id FOREIGN KEY (source_id) REFERENCES source (id);
COMMIT;

BEGIN;
ALTER TABLE payment_instruction
DROP CONSTRAINT IF EXISTS uq_payment_instruction_source_id_idempotency_key;
ALTER TABLE payment_instruction
ADD CONSTRAINT uq_payment_instruction_source_id_idempotency_key UNIQUE (source_id, idempotency_key);
COMMIT;

	metricsErrorReporters metrics.ErrorMetricsReporter,
) func(w http.ResponseWriter, r *http.Request, ps httprouter.Params) {
	return func(w http.ResponseWriter, r *http.Request, ps httprouter.Params) {
		// request
		requestBody, err := pkghttp.DecodeJSON[createPaymentInstructionRequest](w, r)
		if err != nil {
			metricsErrorReporters(ERROR_METRIC_KEY_INVALID_REQUEST_BODY)
			logger.Error(err)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:   INVALID_REQUEST_ERROR_KEY,
				Title: err.Error(),
			})
			return
		}

		// get headers
		customerId, err := uuid.FromString(r.Header.Get("x-customer-id"))
		if err != nil {
			metricsErrorReporters(ERROR_METRIC_KEY_INVALID_CUSTOMER_ID)
			invalidCustomerIdError := InvalidCreatePaymentInstructionRequestError{INVALID_CUSTOMER_ID_MESSAGE}
			logger.Error(invalidCustomerIdError, sentrylogger.KV("customer_id", r.Header.Get("x-customer-id")))
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:   INVALID_REQUEST_ERROR_KEY,
				Title: invalidCustomerIdError.Error(),
			})
			return
		}
		headers := createPaymentInstructionHeaders{
			SourceKey:      r.Header.Get("x-source-key"),
			ClientKey:      r.Header.Get("x-client-key"),
			CustomerId:     customerId,
			IdempotencyKey: r.Header.Get("idempotency-key"),
		}

		if requestBody.AmountCents <= 0 {
			metricsErrorReporters(ERROR_METRIC_KEY_INVALID_AMOUNT_CENTS)
			invalidCreatePaymentInstructionRequestError := InvalidCreatePaymentInstructionRequestError{INVALID_AMOUNT_MESSAGE}
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:   INVALID_REQUEST_ERROR_KEY,
				Title: invalidCreatePaymentInstructionRequestError.Error(),
			})
			return
		}

		// validate payment instruction type
		if requestBody.Type == "" {
			metricsErrorReporters(ERROR_METRIC_KEY_EMPTY_PAYMENT_INSTRUCTION_TYPE)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:         INVALID_REQUEST_ERROR_KEY,
				Title:       core.INVALID_PAYMENT_TYPE_REQUIRED,
				Description: "Payment instruction type is required and cannot be empty",
			})
			return
		}

		// validate supported payment types
		if requestBody.Type != string(core.PAYMENT_INSTRUCTION_TYPE_SALE) && requestBody.Type != string(core.PAYMENT_INSTRUCTION_TYPE_ACCOUNT_PAYMENT) {
			metricsErrorReporters(ERROR_METRIC_KEY_UNSUPPORTED_PAYMENT_INSTRUCTION_TYPE)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:         INVALID_REQUEST_ERROR_KEY,
				Title:       core.INVALID_PAYMENT_TYPE_UNSUPPORTED,
				Description: "Payment instruction type must be either SALE or ACCOUNT_PAYMENT. Requested type: " + requestBody.Type,
			})
			return
		}

		// get source config for payment type validation
		sourceConfig, err := sourceConfigRepository.GetBySourceKey(headers.SourceKey)
		if err != nil {
			metricsErrorReporters(ERROR_METRIC_KEY_SOURCE_CONFIG_NOT_FOUND)
			logger.Error(err)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:   INVALID_REQUEST_ERROR_KEY,
				Title: err.Error(),
			})
			return
		}

		// validate SALE payment instruction type against source config
		if requestBody.Type == string(core.PAYMENT_INSTRUCTION_TYPE_SALE) && !sourceConfig.AllowSales {
			metricsErrorReporters(ERROR_METRIC_KEY_PAYMENT_INSTRUCTION_TYPE_SALE_NOT_ALLOWED)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:         INVALID_REQUEST_ERROR_KEY,
				Title:       core.INVALID_PAYMENT_TYPE_SALE_NOT_ALLOWED,
				Description: "SALE payment type is not allowed for source: " + headers.SourceKey,
			})
			return
		}

		// validate ACCOUNT_PAYMENT payment instruction type against source config
		if requestBody.Type == string(core.PAYMENT_INSTRUCTION_TYPE_ACCOUNT_PAYMENT) && !sourceConfig.AllowAccountPayment {
			metricsErrorReporters(ERROR_METRIC_KEY_PAYMENT_INSTRUCTION_TYPE_ACCOUNT_PAYMENT_NOT_ALLOWED)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusBadRequest, createPaymentInstructionErrorResponse{
				Key:         INVALID_REQUEST_ERROR_KEY,
				Title:       core.INVALID_PAYMENT_TYPE_ACCOUNT_PAYMENT_NOT_ALLOWED,
				Description: "ACCOUNT_PAYMENT payment type is not allowed for source: " + headers.SourceKey,
			})
			return
		}

		// core (internal business logic)
		createdPaymentInstruction, err := core.CreatePaymentInstruction(
			logger,
			paymentInstructionRepository,
			sourceRepository,
			sourceConfigRepository,
			paymentInstructionEventService,
			requestBody.ToPaymentInstructionInput(headers),
			timeToExpireSeconds,
		)

		if err != nil {
			metricsErrorReporters(ERROR_METRIC_KEY_CREATE_PAYMENT_INSTRUCTION_ERROR)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusInternalServerError, createPaymentInstructionErrorResponse{
				Key:   UNEXPECTED_ERROR_KEY,
				Title: err.Error(),
			})
			return
		}

		paymentInstruction, err := core.GetPaymentInstruction(paymentInstructionRepository, createdPaymentInstruction.Id)
		if err != nil {
			metricsErrorReporters(ERROR_METRIC_KEY_GET_PAYMENT_INSTRUCTION_ERROR)
			logger.Error(err)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusInternalServerError, getPaymentInstructionErrorResponse{
				Key:   UNEXPECTED_ERROR_KEY,
				Title: err.Error(),
			})
			return
		}
		if paymentInstruction == nil {
			metricsErrorReporters(ERROR_METRIC_KEY_PAYMENT_INSTRUCTION_NOT_FOUND)
			pkghttp.WriteJSONErrorResponse(logger, w, r, http.StatusNotFound, getPaymentInstructionErrorResponse{
				Key:   INVALID_PAYMENT_DATA_ERROR_KEY,
				Title: "Could not find payment instruction with id " + createdPaymentInstruction.Id.String(),
			})
			return
		}

		metricsReporter(
			map[string]string{
				"method": CREATE_PAYMENT_INSTRUCTION_METHOD,
				"status": string(paymentInstruction.Status),
			})
		// response (transform, encode & write)
		response := createPaymentInstructionResponse{}.FromPaymentInstruction(*paymentInstruction, bashpayUrl, headers.SourceKey)
		pkghttp.WriteJSONSuccessResponse(logger, w, r, http.StatusCreated, response, nil)
	}
}

