package middleware

import (
	pkghttp "bash-payments-payserv/pkg/http"
	pkgkafka "bash-payments-payserv/pkg/kafka"
	sentrylogger "bash-payments-payserv/pkg/sentry-logger"
	"bytes"
	"encoding/json"
	"fmt"
	"io"
	"net/http"

	"github.com/confluentinc/confluent-kafka-go/v2/kafka"

	"github.com/julienschmidt/httprouter"
)

// TODO: remove if not used
func LogRequestToKafka(logger sentrylogger.Logger, kafkaProducer *kafka.Producer, topic string) func(httprouter.Handle) httprouter.Handle {
	return func(next httprouter.Handle) httprouter.Handle {
		return func(w http.ResponseWriter, r *http.Request, ps httprouter.Params) {

			// ... read request body
			body, err := io.ReadAll(r.Body)
			if err != nil {
				logger.Error(pkghttp.RequestBodyReadError{Message: fmt.Sprintf("failed to produce audit log to %v when reading request body: %v", topic, err)})
				w.WriteHeader(http.StatusBadRequest)
				return
			}

			defer r.Body.Close()
			// io.ReadAll results in the body not being able to be read again
			// therefore we replace the body with a new reader after reading from the original
			r.Body = io.NopCloser(bytes.NewBuffer(body))

			// ... unmarshal request body
			bodyMap := make(map[string]interface{})
			err = json.Unmarshal(body, &bodyMap)
			if err != nil {
				logger.Error(pkghttp.RequestBodyUnmarshalError{Message: fmt.Sprintf("failed to produce audit log to %v when parsing json: %v", topic, err)})
				w.WriteHeader(http.StatusBadRequest)
				return
			}

			// ... produce to kafka
			err = kafkaProducer.Produce(&kafka.Message{
				TopicPartition: kafka.TopicPartition{Topic: &topic, Partition: kafka.PartitionAny},
				Value:          body,
			}, nil)

			if err != nil {
				logger.Error(pkgkafka.KafkaProducerError{Message: fmt.Sprintf("failed to produce audit log to %v: %v", topic, err)})
				w.WriteHeader(http.StatusBadRequest)
				return
			}

			// ... call handler
			next(w, r, ps)
		}
	}
}

