package db

import (
	"bash-payments-payserv/internal/core"
	sentrylogger "bash-payments-payserv/pkg/sentry-logger"
	"database/sql"

	"github.com/gofrs/uuid"
)

type SourceRepository struct {
	Db     core.Db
	Logger sentrylogger.Logger
}

func (repo *SourceRepository) GetByKey(key string) (*core.Source, error) {
	query := `
	SELECT id, key, description FROM source WHERE key = $1;`
	result := core.Source{}
	err := repo.Db.QueryRow(query, key).Scan(&result.Id, &result.Key, &result.Description)
	if err != nil && err == sql.ErrNoRows {
		return nil, nil
	}
	if err != nil {
		repo.Logger.Error(err)
		return nil, err
	}
	return &result, nil
}

func (repo *SourceRepository) GetById(id uuid.UUID) (*core.Source, error) {
	query := `
	SELECT id, key, description FROM source WHERE id = $1;`
	result := core.Source{}
	err := repo.Db.QueryRow(query, id).Scan(&result.Id, &result.Key, &result.Description)
	if err != nil && err == sql.ErrNoRows {
		return nil, nil
	}
	if err != nil {
		repo.Logger.Error(err)
		return nil, err
	}
	return &result, nil
}

