import type { InstanceOptions, IOContext, IOResponse, Maybe } from '@vtex/api'
import { ExternalClient } from '@vtex/api'
import type { CustomField } from '@vtex/payment-provider'

import { config } from '../shared/constants'
import { getMerchantSetting } from '../shared/utils'
import { v5 } from 'uuid'

const DEFAULT_SOURCE_KEY = 'CHECKOUT'
const UUID_NAMESPACE = 'f33b6097-403d-4eae-8c29-19650db342d8'

export class PayservClient extends ExternalClient {
  constructor(context: IOContext, options?: InstanceOptions) {
    super(
      context.account === config.PRODUCTION_ACCOUNT
        ? config.PAYSERVE_API
        : config.PAYSERVE_API_STAGING,
      context,
      {
        ...options,
        headers: {
          'X-Vtex-Use-Https': 'true',
          contentType: 'application/json',
          Accept: 'application/json',
        },
      }
    )
  }

  public CreatePayment = async (
    ctx: Context,
    body: PayservBashpayPaymentInstruction,
    merchantSettings: Maybe<CustomField[]>,
    customerID: string,
    client: string,
    sourceKey: string
  ): Promise<IOResponse<PayservPaymentInstructionResponse>> => {
    const {
      vtex: { logger },
    } = ctx

    logger.info(`getting api key`)
    const apiKey = getMerchantSetting('BashPay BFF API Key', merchantSettings)
    const appID = getMerchantSetting('BashPay BFF APP ID', merchantSettings)

    const headers: HeadersPayserv = {
      'idempotency-key': v5(body.source_reference, UUID_NAMESPACE),
      'x-api-key': apiKey,
      'x-app-id': appID,
      'x-source-key': sourceKey !== '' ? sourceKey : DEFAULT_SOURCE_KEY,
      'x-client-key': client,
      'x-customer-id': customerID,
    }

    try {
      logger.info({message: `posting to payserv`, data: JSON.stringify(body) })
      const response = await this.http.postRaw<PayservPaymentInstructionResponse>(
        'payment_instructions',
        body,
        { headers }
      )

      logger.info({
        message: 'Post to payserv success',
        data: {
          Request: JSON.stringify(body),
          Response: JSON.stringify(response.data),
          Status: response.status,
        },
      })

      return response
    } catch (err) {
      logger.error({
        message: 'Error posting to payserv',
        data: {
          Request: JSON.stringify(body),
          Response: err,
          Headers: JSON.stringify(headers),
        },
        exception: err,
      })
      throw err
    }
  }
}

