type createPaymentInstructionRequest struct {
	AmountCents              json.Number `json:"amount_cents"` // sent from connector
	Type                     string      `json:"type"`
	SourceReference          string      `json:"source_reference"`
	SourceTransactionId      string      `json:"source_transaction_id"`
	SourcePaymentId          string      `json:"source_payment_id"`
	SourceCallbackUrl        string      `json:"source_callback_url"`
	SourceSuccessRedirectUrl string      `json:"source_success_redirect_url"`
	SourceFailureRedirectUrl string      `json:"source_failure_redirect_url"`
}

func (data createPaymentInstructionRequest) ToPaymentInstructionInput(headers requestHeaders) (core.CreatePaymentInstructionInput, error) {
	amountCents, err := data.AmountCents.Int64() //conversion required as connector sends amount as Number
	if err != nil {
		return core.CreatePaymentInstructionInput{}, err
	}
	return core.CreatePaymentInstructionInput{
		PaymentInstructionWriteInput: core.PaymentInstructionWriteInput{
			IdempotencyKey: headers.IdempotencyKey,
			ClientKey:      headers.ClientKey,
			SourceKey:      headers.SourceKey,
			CustomerId:     headers.CustomerId,
		},
		AmountCents:              amountCents,
		Type:                     data.Type,
		SourceReference:          data.SourceReference,
		SourceTransactionId:      data.SourceTransactionId,
		SourcePaymentId:          data.SourcePaymentId,
		SourceCallbackUrl:        data.SourceCallbackUrl,
		SourceSuccessRedirectUrl: data.SourceSuccessRedirectUrl,
		SourceFailureRedirectUrl: data.SourceFailureRedirectUrl,
	}, nil
}

type createPaymentInstructionResponse struct {
	Id                       uuid.UUID                     `json:"id"`
	AmountCents              int64                         `json:"amount_cents"`
	AmountDueCents           int64                         `json:"amount_due_cents"`
	AmountPaidCents          int64                         `json:"amount_paid_cents"`
	Type                     string                        `json:"type"`
	SourceKey                string                        `json:"source_key"`
	SourceSuccessRedirectUrl string                        `json:"source_success_redirect_url,omitempty"`
	SourceFailureRedirectUrl string                        `json:"source_failure_redirect_url,omitempty"`
	RedirectUrl              string                        `json:"redirect_url"`
	Status                   core.PaymentInstructionStatus `json:"status"`
	IsCancellable            bool                          `json:"is_cancellable"`
	Payments                 []core.Payment                `json:"payments"`
	Message                  *ResponseMessage              `json:"message,omitempty"`
}

type createPaymentInstructionErrorResponse struct {
	Key         string `json:"key"`
	Title       string `json:"title"`
	Description string `json:"description,omitempty"`
}

func (response createPaymentInstructionResponse) FromPaymentInstruction(config config.Config, data core.PaymentInstruction) createPaymentInstructionResponse {
	var message *ResponseMessage
	if data.Message != nil {
		message = &ResponseMessage{
			Key:         data.Message.Key,
			Title:       data.Message.Title,
			Description: data.Message.Description,
		}
	}
	return createPaymentInstructionResponse{
		Id:                       data.Id,
		AmountCents:              data.AmountCents,
		AmountDueCents:           data.AmountDueCents,
		AmountPaidCents:          data.AmountPaidCents,
		Type:                     data.Type,
		SourceKey:                data.SourceKey,
		SourceSuccessRedirectUrl: data.SourceSuccessRedirectUrl,
		SourceFailureRedirectUrl: data.SourceFailureRedirectUrl,
		RedirectUrl:              config.BashPayUri + "?payment-instruction-id=" + data.Id.String(),
		Status:                   data.Status,
		IsCancellable:            data.IsCancellable,
		Payments:                 []core.Payment{},
		Message:                  message,
	}
}

func CreatePaymentInstruction(
	config config.Config,
	logger sentrylogger.Logger,
	payservClient core.PayservHttpClient,
) func(w http.ResponseWriter, r *http.Request, ps httprouter.Params) {
	return func(w http.ResponseWriter, r *http.Request, ps httprouter.Params) {
