package payserv

import (
	"bash-payments-bashpay-bff/internal/clients"
	"bash-payments-bashpay-bff/internal/core"
	"bash-payments-bashpay-bff/pkg/http/client"
	sentrylogger "bash-payments-bashpay-bff/pkg/sentry-logger"
	"context"
	"encoding/json"
	"fmt"
	"net/http"
	"slices"

	"github.com/gofrs/uuid"
)

func buildResponse[T any, U any](genericResponse *client.GenericHttpResponse, transformer func(T) U) (*U, error) {
	var err error = nil
	// ... success
	var response T

	if genericResponse.StatusCode == 204 {
		return nil, nil
	}

	if genericResponse.StatusCode >= 200 && genericResponse.StatusCode < 300 {
		err = json.Unmarshal(genericResponse.Body, &response)
		if err != nil {
			return nil, err
		}
		_response := transformer(response)
		return &_response, nil
	}
	// ... error
	var errResponse ErrorResponse
	err = json.Unmarshal(genericResponse.Body, &errResponse)
	if err != nil {
		return nil, err
	}
	// TODO: return different errors based on errResponse
	if genericResponse.StatusCode == 403 {
		return nil, &PayservClientError{
			Key:   FORBIDDEN_KEY,
			Title: "Forbidden",
		}
	}

	if genericResponse.StatusCode >= 500 {
		return nil, &PayservClientError{
			Key:         errResponse.Error.Key,
			Title:       errResponse.Error.Title,
			Description: errResponse.Error.Description,
		}
	}

	return nil, &PayservClientError{
		Key:         errResponse.Error.Key,
		Title:       errResponse.Error.Title,
		Description: errResponse.Error.Description,
	}
}

func buildListResponse[T any, U any](genericResponse *client.GenericHttpResponse, transformer func(T) []U) ([]U, error) {
	var err error = nil
	// ... success
	var parsedGenericResponse T
	response := []U{}
	if genericResponse.StatusCode >= 200 && genericResponse.StatusCode < 300 {
		err = json.Unmarshal(genericResponse.Body, &parsedGenericResponse)
		if err != nil {
			return response, err
		}
		response := transformer(parsedGenericResponse)
		return response, nil
	}
	// ... error
	var errResponse ErrorResponse
	err = json.Unmarshal(genericResponse.Body, &errResponse)
	if err != nil {
		return response, err
	}

	if genericResponse.StatusCode == 403 {
		return response, &PayservClientError{
			Key:   FORBIDDEN_KEY,
			Title: "Forbidden",
		}
	}
	// TODO: return different errors based on errResponse
	if genericResponse.StatusCode >= 500 {
		return response, &PayservClientError{
			Key:         errResponse.Error.Key,
			Title:       errResponse.Error.Title,
			Description: errResponse.Error.Description,
		}
	}
	return response, &PayservClientError{
		Key:         errResponse.Error.Key,
		Title:       errResponse.Error.Title,
		Description: errResponse.Error.Description,
	}
}

type PayservHttpClient struct {
	Logger     sentrylogger.Logger
	HttpClient clients.HttpClient
	Uri        string
	AppId      string
	ApiKey     string
}

func (c *PayservHttpClient) buildHeaders(ctx context.Context, customerId uuid.UUID, clientKey string, sourceKey string, idempotencyKey *uuid.UUID) map[string]string {
	headers := map[string]string{
		"Content-Type": "application/json",
		"x-client-key": clientKey,
		"x-source-key": sourceKey,
		"x-app-id":     c.AppId,
		"x-api-key":    c.ApiKey,
	}
	if customerId != uuid.Nil {
		headers["x-customer-id"] = customerId.String()
	}
	if idempotencyKey != nil {
		headers["Idempotency-Key"] = idempotencyKey.String()
	}
	userIp := ctx.Value("user_ip")
	if userIp != nil {
		headers["x-user-ip"] = userIp.(string)
	}
	return headers
}

func (c PayservHttpClient) CreatePaymentInstruction(ctx context.Context, input core.CreatePaymentInstructionInput) (*core.PaymentInstruction, error) {
	// request
	headers := c.buildHeaders(ctx, input.CustomerId, input.ClientKey, input.SourceKey, input.IdempotencyKey)
	body := CreatePaymentInstructionRequest{}.FromPaymentInstructionRequest(input)
	genericResponse, err := c.HttpClient.Request(client.GenericHttpRequest{
		Method:  http.MethodPost,
		Url:     c.Uri + "/v1/payment_instructions",
		Headers: headers,
		Body:    body,
	})
	if err != nil {
		c.Logger.Error(err)
		return nil, err
	}

	// response
	transformer := func(response PaymentInstructionResponse) core.PaymentInstruction {
		return response.ToPaymentInstruction()
	}
	return buildResponse[PaymentInstructionResponse, core.PaymentInstruction](genericResponse, transformer)
}

func (c PayservHttpClient) GetPaymentInstructionById(ctx context.Context, input core.PaymentInstructionReadInput, id uuid.UUID) (*core.PaymentInstruction, error) {
	// request
	headers := c.buildHeaders(ctx, input.CustomerId, input.ClientKey, input.SourceKey, nil)
	genericResponse, err := c.HttpClient.Request(client.GenericHttpRequest{
		Method:                  http.MethodGet,
		Url:                     c.Uri + "/v1/payment_instructions/" + id.String(),
		Headers:                 headers,
		QueryParams:             map[string]string{},
		CardinalityFriendlyPath: "/v1/payment_instructions/:instruction_id",
	})
	if err != nil {
		c.Logger.Error(err)
		return nil, err
	}

	// response
	transformer := func(response PaymentInstructionResponse) core.PaymentInstruction {
		return response.ToPaymentInstruction()
	}
	return buildResponse[PaymentInstructionResponse, core.PaymentInstruction](genericResponse, transformer)
}

func (c PayservHttpClient) CancelPaymentInstruction(ctx context.Context, input core.PaymentInstructionWriteInput, id uuid.UUID) (*uuid.UUID, error) {
	// request
	headers := c.buildHeaders(ctx, input.CustomerId, input.ClientKey, input.SourceKey, input.IdempotencyKey)
	genericResponse, err := c.HttpClient.Request(client.GenericHttpRequest{
		Method:                  http.MethodPatch,
		Url:                     c.Uri + "/v1/payment_instructions/" + id.String() + "/cancel",
		Headers:                 headers,
		CardinalityFriendlyPath: "/v1/payment_instructions/:instruction_id/cancel",
	})
	if err != nil {
		c.Logger.Error(err)
		return nil, err
	}

	// response
	transformer := func(response CancelPaymentInstructionResponse) uuid.UUID {
		return response.Data.Id
	}
	return buildResponse[CancelPaymentInstructionResponse, uuid.UUID](genericResponse, transformer)
}

func (c PayservHttpClient) CreatePayment(ctx context.Context, input core.CreatePaymentInput, paymentInstructionId uuid.UUID) (*core.Payment, error) {
	// request
	headers := c.buildHeaders(ctx, input.CustomerId, input.ClientKey, input.SourceKey, input.IdempotencyKey)
